package day07_Operators;import java.util.*;public class SecondToMinutesToHourToDayToYear {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        System.out.println("Enter seconds:");        int inputSecond = scan.nextInt();        int year = inputSecond / (60 * 60) / 24 / 365;        int day = (inputSecond / 60 / 60 / 24) % 365;        int hour = (inputSecond / 60 / 60) % 24;        int minute = (inputSecond / 60) % 60;        int second = inputSecond % 60;        System.out.println(year + " Year " + day + " day " + hour + " hours, " + minute + " minutes, and " + second + " seconds");    }}