package day10_NestedIf;import java.sql.SQLOutput;import java.util.Scanner;public class Calculator {    public static void main(String[] args) {/*        2.Create a class called Calculator, Given two double variables named n1 & n2,and a char variable named        mathOperator, write a program that can calculate result of n1&n2 based on the given math Operator.        char operator -->   -.+.*,        when operator is +: add num1.num2        when operator- : minus numl, num2        when operator: * multiply numl,num2        when operator:/: divide num1,num2        for any other operators: print "invalid operator"        EX:        n1 = 10, n2= 20, mathOperator = '+'        output:        30*/        Scanner scan = new Scanner(System.in);        double result = 0;        System.out.println(" Please Enter First Number");        double n1 = scan.nextDouble();        System.out.println("Please Enter Second Number");        double n2 = scan.nextDouble();        System.out.println(" Please Enter Math Operator (-,+,*,/)");        String mathOperator = scan.next();        if(mathOperator.equals("+") || mathOperator.equals("-")||mathOperator.equals("*")||mathOperator.equals("/")) {            if (mathOperator.equals("+")) {                result = (n1 + n2);            }else if (mathOperator.equals("-")) {                result = (n1 - n2);            }else if (mathOperator.equals("*")) {                result = (n1 * n2);            }else if (mathOperator.equals("/")) {                result = (n1 / n2);            }            System.out.println(n1+" "+ mathOperator+" " +n2 +" = "+ result);        }else{            System.out.println("Invalid Math Operators");        }    }}