package day10_NestedIf;public class CrewAndPassangers {    public static void main(String[] args) {        int total = 51;        String result = "";        int crew = 0, passenger=0;        if (total >=50) {            crew = 20;            passenger = 30;            if (total>=75) {                crew = 25;                passenger = 50;                if (total == 100) {                    crew = 30;                    passenger = 70;                }            }        }else{            System.out.println("Any other number of people on the ship is not valid");        }        result = ("total = " +total+" crew = "+ crew+" passenger = "+passenger);        System.out.println(result);    }}