package day10_NestedIf;public class TernaryNestedIfGradeLevel {    public static void main(String[] args) {        /*        90 ~ 100: Excellent        80~ 89: Great        70~ 79: Good        60 ~ 69: Passed        0~ 59: failed                */        int score = 90;        String grade = (score >= 0 && score <= 100) ? (score >= 90) ? "Excellent" : (score >= 80) ? "Great"                : (score >= 70) ? "Good" : (score >= 60) ? "Passed" : "Failed" : "Invalid Score";        System.out.println("Grade = " + grade);    }}