package day18_NestedLoop;import java.util.Scanner;public class Login_ForLoop {    public static void main(String[] args) {        Scanner scan =new Scanner(System.in);        System.out.println("Enter your username");        String u =scan.next();        System.out.println("Enter your password");        String p =scan.next();        if (u.equalsIgnoreCase("Cydeo") && p.equals("WoodenSpoon")){            System.out.println("Logged in");        }else{            for (int i = 0; i < 3 ; i++) {                System.err.println(" Incorrect username or Password. You have " +(3-i)+" more attempts");                System.out.println("Enter yor username");                u=scan.next();                System.out.println("Enter yor password");                p=scan.next();                if (u.equalsIgnoreCase("Cydeo") && p.equals("WoodenSpoon")){                    System.out.println("Logged in");                    break;                }                System.err.println("Your account blocked. Please contact with \"Cydeo Support Team\"");            }            scan.close();        }        /*         2. You are writing a code for the log-in function of the Cydeo Application, assume that your credentials are:                            username: Cydeo                            password: WoodenSpoon                Ask the user to enter their credentials.                        If credentials are matched, your program should print "Logged in."                        If the credentials are not matched, the program should allow the user to have three attempts                        to enter correct credentials and if all three attempts are failed, then print "Your account is locked."         */    }}