package day18_NestedLoop;import java.util.Scanner;public class OddEvenWith_DoWhile_Loop {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        String yesOrNo;        do {            System.out.println("Enter a number");            int number = scan.nextInt();            if (number % 2 == 0) {                System.out.println(number + " is even number");            } else {                System.out.println(number + " is odd number");            }            System.out.println("Would you like to enter another number");            yesOrNo = scan.next().toLowerCase();            while (!(yesOrNo.equals("yes") || yesOrNo.equals("no"))){                System.out.println("Invalid answer, please re-enter, Would you like to enter another number yes or no");                yesOrNo = scan.next().toLowerCase();            }        } while (yesOrNo.equals("yes"));        System.out.println("bye bye");        /*        2. Write a program that asks user to enter a number , and check if the number is odd or even.        if it's odd, print "Odd Number"        if it's even, print "Even number"        Ask the user "Would you like to enter another number"                if user enters yes, repeat the previous steps                if user enters no, print nothing                If user enteres invalid answer, repeat the previous steps         */    }}