package day18_NestedLoop;import java.util.Scanner;public class OddEvenWith_WhileLoop {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        while (true) {            System.out.println("enter a number");            int num = scan.nextInt();            if (num % 2 == 0) {                System.out.println(num + " is even number");            } else {                System.out.println(num + " is odd number");            }            System.out.println("Would youlike to another number yes or no");            String answer = scan.next();            answer = answer.toLowerCase();            while ( !(answer.equals("yes") || answer.equals("no")) ) {                System.out.println("Insvalid answer Please re-enter, would ou like to enter another number");                answer = scan.next().toLowerCase();            }            if (answer.equals("no")) {                break;            }        }        /*        2. Write a program that asks user to enter a number , and check if the number is odd or even.        if it's odd, print "Odd Number"        if it's even, print "Even number"        Ask the user "Would you like to enter another number"                if user enters yes, repeat the previous steps                if user enters no, print nothing                If user enteres invalid answer, repeat the previous steps         */    }}