package day20_Arrays;import java.util.Arrays;import java.util.Scanner;public class MinNumberOfArrayWithUserEntering {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        System.out.println(" How many Number Do you want to Enter");        byte length = scan.nextByte();        byte[] numbers = new byte[length];        byte min = 127;        for (byte i = length, j=0; i > 0 && j<length; i--,j++) {            System.out.println("Enter a number (remain : " + i+")");            numbers[j] = scan.nextByte();            if (numbers[j]<min){                min = numbers[j];            }        }        System.out.println("minimim number is : "+min);    }}