package day25_CustomMethod_MethodOverloading;import java.util.Arrays;public class _06_MergeTwoArray {    public static void main(String[] args) {        int[] arr1 = {1, 2, 3, 4, 5};        int[] arr2 = {6, 7, 8, 9, 10};        System.out.println(Arrays.toString(mergeTwoArray(arr1,arr2)));    }    public static int[] mergeTwoArray(int[] arr1, int[] arr2) {        int[] mergeArray = new int[arr1.length + arr2.length ];        int i = 0;        for (int each : arr1) {            mergeArray[i++] = each;        }        for (int each : arr2) {            mergeArray[i++] = each;        }        return mergeArray;    }}