package day26_CustomMethodPractice;import utility.ArraysUtility;import java.util.Arrays;public class _07_RemoveDuplicates {    public static void main(String[] args) {        int[] arr = {1, 1, 1, 3,3,3,5,5,5,6,6,6,7,7};        int[] result = removeDuplicates(arr);        System.out.println(Arrays.toString(result));    }    public static int[] removeDuplicates(int[] array) {        int[] result = {};        for (int i = 0; i < array.length; i++) {            int count = 0;            for (int j = i + 1; j < array.length; j++) {                if (array[i] == array[j]) {                    count++;                }            }            if (count == 0) {                result = ArraysUtility.addElement(result, array[i]);            }        }        return result;    }}