package day31_Constructors._1_scrumTask;public class _1_Tester {    public String name;    public int employeeID;    public String jobTitle;    public double salary;    public _1_Tester(String name, int employeeID, String jobTitle, double salary) {        this.name = name;        this.employeeID = employeeID;        this.jobTitle = jobTitle;        this.salary = salary;    }    public String toString() {        return "Tester{" +                "name='" + name + '\'' +                ", employeeID=" + employeeID +                ", jobTitle='" + jobTitle + '\'' +                ", salary= $" + salary +                '}';    }    public void smokeTesting(){        System.out.println(name + " is smoking testing");    }    public void creatingTicket(){        System.out.println(name + " is creating  testing");    }    public void dailyStandUp(){        System.out.println(name + " is doing daily Standn Up Meeting");    }}/*Scrum Task:	create a class called Tester	        Attributes:	            name, employeeID, JobTitle, Salary	        Add A constructor that can set all the fields	        Actions:	           smokeTesting(),  creatingTicket(), dailyStandUp() toString() */