package day31_Constructors._2_Tasks;public class _07_SalaryCalculator {    public float hourlyRate, stateTaxRate, federalTaxRate, weeklyHours;    public _07_SalaryCalculator(float hourlyRate, float stateTaxRate, float federalTaxRate, float weeklyHours) {        this.hourlyRate = hourlyRate;        this.stateTaxRate = stateTaxRate;        this.federalTaxRate = federalTaxRate;        this.weeklyHours = weeklyHours;    }    public float salary(){        return hourlyRate * weeklyHours * 52;    }    public float stateTax(){        return salary()*stateTaxRate;    }    public float federalTax(){        return salary()*federalTaxRate;    }    public float salaryAfterTax(){        return salary() - stateTax() - federalTax();    }    public String toString() {        return "_07_SalaryCalculator{" +                "salary= $" + salary() +                ", stateTax= $" + stateTax() +                ", federalTax= $" + federalTax() +                ", salary after tax=" + salaryAfterTax() +                '}';    }}/*1. SalaryCalculator Task:		1.1 Create a class named Salary calculator:		        Attributes:		            hourlyRate, stateTaxRate, federalTaxRate, weeklyHours	            Add a constructor to set all the fields		        Actions:		            salary(): calculates the salary ( hourlyRate * weeklyHour * 52)		            stateTax(): calculates the totalStateTax		            federalTax(): calculates the total federal tax		            salaryAfterTax(): calculates the salary after tax		            toString(): displays the salary, stateTax, federalTax, salaryAfterTax of the Object */