package day31_Constructors._2_Tasks;public class _10_Item {    String name;    double unitPrice;    int quantity;    public _10_Item(String name, double unitPrice, int quantity) {        this.name = name;        this.unitPrice = unitPrice;        this.quantity = quantity;    }    public double calcCost() {        return unitPrice * quantity;    }    public String toString() {        return "_10_Item{" +                "name='" + name + '\'' +                ", unitPrice=" + unitPrice +                ", quantity=" + quantity +                ", total price = $" + calcCost() +                '}';    }}